		/// <summary>Initialize using values in Properties file.</summary>
		/// <param name="props">The properties object used for initialization</param>
		/// <param name="printProps">Whether to print the properties to stderr as it works.</param>
		public virtual void SetProperties(Properties props, bool printProps)
		{
			this.props = props;
			StringBuilder sb = new StringBuilder(stringRep);
			foreach (string key in props.StringPropertyNames())
			{
				string val = props.GetProperty(key);
				if (!(key.IsEmpty() && val.IsEmpty()))
				{
					if (printProps)
					{
						log.Info(key + '=' + val);
					}
					sb.Append(key).Append('=').Append(val).Append('\n');
				}
				if (Sharpen.Runtime.EqualsIgnoreCase(key, "macro"))
				{
					if (bool.ParseBoolean(val))
					{
						useObservedSequencesOnly = true;
						readerAndWriter = "edu.stanford.nlp.sequences.CoNLLDocumentReaderAndWriter";
						// useClassFeature = true;
						// submit
						useLongSequences = true;
						useTaggySequences = true;
						useNGrams = true;
						usePrev = true;
						useNext = true;
						useTags = true;
						useWordPairs = true;
						useSequences = true;
						usePrevSequences = true;
						// noMidNGrams
						noMidNGrams = true;
						// reverse
						useReverse = true;
						// typeseqs3
						useTypeSeqs = true;
						useTypeSeqs2 = true;
						useTypeySequences = true;
						// wordtypes2 && known
						wordShape = WordShapeClassifier.Wordshapedan2uselc;
						// occurrence
						useOccurrencePatterns = true;
						// realword
						useLastRealWord = true;
						useNextRealWord = true;
						// smooth
						sigma = 3.0;
						// normalize
						normalize = true;
						normalizeTimex = true;
					}
				}
				else
				{
					if (Sharpen.Runtime.EqualsIgnoreCase(key, "goodCoNLL"))
					{
						// This was developed for CMMClassifier after the original 2003 CoNLL work.
						// It is for an MEMM.  You shouldn't use it with CRFClassifier.
						if (bool.ParseBoolean(val))
						{
							// featureFactory = "edu.stanford.nlp.ie.NERFeatureFactory";
							readerAndWriter = "edu.stanford.nlp.sequences.CoNLLDocumentReaderAndWriter";
							useObservedSequencesOnly = true;
							// useClassFeature = true;
							useLongSequences = true;
							useTaggySequences = true;
							useNGrams = true;
							usePrev = true;
							useNext = true;
							useTags = true;
							useWordPairs = true;
							useSequences = true;
							usePrevSequences = true;
							// noMidNGrams
							noMidNGrams = true;
							// should this be set?? maxNGramLeng = 6; No (to get best score).
							// reverse
							useReverse = false;
							// typeseqs3
							useTypeSeqs = true;
							useTypeSeqs2 = true;
							useTypeySequences = true;
							// wordtypes2 && known
							wordShape = WordShapeClassifier.Wordshapedan2uselc;
							// occurrence
							useOccurrencePatterns = true;
							// realword
							useLastRealWord = true;
							useNextRealWord = true;
							// smooth
							// This was originally 20, but in Aug 2006 increased to 50, because that helped
							// for English, but actually even smaller than 20 helps for languages like
							// Spanish, so dropped in 2014 to 5.0.
							sigma = 5.0;
							// normalize
							normalize = true;
							normalizeTimex = true;
							// this was sort of wrong for German since it lowercases months, but didn't do too much harm
							maxLeft = 2;
							useDisjunctive = true;
							disjunctionWidth = 4;
							// clearly optimal for CoNLL
							useBoundarySequences = true;
							useLemmas = true;
							// no-op except for German
							usePrevNextLemmas = true;
							// no-op except for German
							strictGoodCoNLL = true;
							// don't add some CpC features added later
							removeStrictGoodCoNLLDuplicates = true;
							// added in 2014; the duplicated features don't help
							inputEncoding = "iso-8859-1";
							// needed for CoNLL German and Spanish files
							// optimization
							useQN = true;
							QNsize = 15;
						}
					}
					else
					{
						if (Sharpen.Runtime.EqualsIgnoreCase(key, "conllNoTags"))
						{
							if (bool.ParseBoolean(val))
							{
								readerAndWriter = "edu.stanford.nlp.sequences.ColumnDocumentReaderAndWriter";
								// trainMap=testMap="word=0,answer=1";
								map = "word=0,answer=1";
								useObservedSequencesOnly = true;
								// useClassFeature = true;
								useLongSequences = true;
								// useTaggySequences = true;
								useNGrams = true;
								usePrev = true;
								useNext = true;
								// useTags = true;
								useWordPairs = true;
								useSequences = true;
								usePrevSequences = true;
								// noMidNGrams
								noMidNGrams = true;
								// reverse
								useReverse = false;
								// typeseqs3
								useTypeSeqs = true;
								useTypeSeqs2 = true;
								useTypeySequences = true;
								// wordtypes2 && known
								wordShape = WordShapeClassifier.Wordshapedan2uselc;
								// occurrence
