		// end setProperties()
		// Thang Sep13: refactor to be used for multiple factories.
		private static string GetFeatureFactory(string val)
		{
			if (Sharpen.Runtime.EqualsIgnoreCase(val, "SuperSimpleFeatureFactory"))
			{
				val = "edu.stanford.nlp.sequences.SuperSimpleFeatureFactory";
			}
			else
			{
				if (Sharpen.Runtime.EqualsIgnoreCase(val, "NERFeatureFactory"))
				{
					val = "edu.stanford.nlp.ie.NERFeatureFactory";
				}
				else
				{
					if (Sharpen.Runtime.EqualsIgnoreCase(val, "GazNERFeatureFactory"))
					{
						val = "edu.stanford.nlp.sequences.GazNERFeatureFactory";
					}
					else
					{
						if (Sharpen.Runtime.EqualsIgnoreCase(val, "IncludeAllFeatureFactory"))
						{
							val = "edu.stanford.nlp.sequences.IncludeAllFeatureFactory";
						}
						else
						{
							if (Sharpen.Runtime.EqualsIgnoreCase(val, "PhraseFeatureFactory"))
							{
								val = "edu.stanford.nlp.article.extraction.PhraseFeatureFactory";
							}
							else
							{
								if (Sharpen.Runtime.EqualsIgnoreCase(val, "EmbeddingFeatureFactory"))
								{
									val = "edu.stanford.nlp.ie.EmbeddingFeatureFactory";
								}
							}
						}
					}
				}
			}
			return val;
		}

		/// <summary>Print the properties specified by this object.</summary>
		/// <returns>A String describing the properties specified by this object.</returns>
		public override string ToString()
		{
			return stringRep;
		}

		/// <summary>
		/// note that this does *not* return string representation of arrays, lists and
		/// enums
		/// </summary>
		/// <exception cref="System.MemberAccessException"/>
		/// <exception cref="System.ArgumentException"/>
		public virtual string GetNotNullTrueStringRep()
		{
			try
			{
				StringBuilder rep = new StringBuilder();
				string joiner = "\n";
				FieldInfo[] f = this.GetType().GetFields();
				foreach (FieldInfo ff in f)
				{
					string name = ff.Name;
					Type type = ff.GetType();
					if (type.Equals(typeof(bool)) || type.Equals(typeof(bool)))
					{
						bool val = ff.GetBoolean(this);
						if (val)
						{
							rep.Append(joiner).Append(name).Append('=').Append(val);
						}
					}
					else
					{
						if (type.Equals(typeof(string)))
						{
							string val = (string)ff.GetValue(this);
							if (val != null)
							{
								rep.Append(joiner).Append(name).Append('=').Append(val);
							}
						}
						else
						{
							if (type.Equals(typeof(double)))
							{
								double val = (double)ff.GetValue(this);
								rep.Append(joiner).Append(name).Append('=').Append(val);
							}
							else
							{
								if (type.Equals(typeof(double)))
								{
									double val = ff.GetDouble(this);
									rep.Append(joiner).Append(name).Append('=').Append(val);
								}
								else
								{
									if (type.Equals(typeof(int)))
									{
										int val = (int)ff.GetValue(this);
										rep.Append(joiner).Append(name).Append('=').Append(val);
									}
									else
									{
										if (type.Equals(typeof(int)))
										{
											int val = ff.GetInt(this);
											rep.Append(joiner).Append(name).Append('=').Append(val);
										}
										else
										{
											if (type.Equals(typeof(float)))
											{
												float val = (float)ff.GetValue(this);
												rep.Append(joiner).Append(name).Append('=').Append(val);
											}
											else
											{
												if (type.Equals(typeof(float)))
												{
													float val = ff.GetFloat(this);
													rep.Append(joiner).Append(name).Append('=').Append(val);
												}
												else
												{
													if (type.Equals(typeof(byte)))
													{
														byte val = (byte)ff.GetValue(this);
														rep.Append(joiner).Append(name).Append('=').Append(val);
													}
													else
													{
														if (type.Equals(typeof(byte)))
														{
															byte val = ff.GetByte(this);
															rep.Append(joiner).Append(name).Append('=').Append(val);
														}
														else
														{
															if (type.Equals(typeof(char)))
															{
																char val = ff.GetChar(this);
																rep.Append(joiner).Append(name).Append('=').Append(val);
															}
															else
															{
																if (type.Equals(typeof(long)))
																{
																	long val = (long)ff.GetValue(this);
																	rep.Append(joiner).Append(name).Append('=').Append(val);
																}
																else
																{
																	if (type.Equals(typeof(long)))
																	{
																		long val = ff.GetLong(this);
																		rep.Append(joiner).Append(name).Append('=').Append(val);
																	}
																}
															}
														}
													}
												}
											}
										}
									}
								}
							}
						}
					}
				}
				return rep.ToString();
			}
			catch (Exception e)
			{
				Sharpen.Runtime.PrintStackTrace(e);
				return string.Empty;
			}
		}
	}
